import React, {useState, useEffect} from "react"; 
import axios from "axios";
import Exercise from "./Exercise.js";
import Popup from './Popup';
import "./style.css";
import xtype from 'xtypejs';


export default function Tblu({isOpen}) {
    const [IsOpenp, setIsOpenp] = useState(isOpen);
    const [isOpen1, setIsOpen] = useState(false);
    const [another, setAnother] = useState("");
    const [elem, setElem] = useState([]);
    const [name, setName] = useState();
    const [isActive, setIsActive] = useState(false);
    const [isOnclick, setIsOnclick] = useState("");


    const changeHandler = (e) => {
		setName(e.target.value)
	}

    const handleClick = () => {
        setIsOpen(!isOpen);
        setIsActive(current => !current);
    }

    const togglePopup = () => {
        setIsOpenp(!IsOpenp);
    }

    const getElem = () => {
        axios.get('http://localhost:5000/')
        .then(res => {
            const elems = res.data
            console.log(elems)
            if (xtype.type(elems) === "sting"){
                setAnother(elems)
            }
            setElem(elems)
        })
    }

    const submit = (e) => {
		const data = new FormData() 
        data.append('name', name)
		e.preventDefault()

		axios.post('http://127.0.0.1:5000/add', data,  {})	
		.then(() => {
			getElem()
		})	
		.catch(error => console.error(error))
	}

    useEffect(() => {
        getElem()
    }, []);

    return (
        <div>
            <p>{another}</p>
            <button
                style={{
                    display: isActive ? 'none' : '',
                }}
                type="submit" 
                onClick={setIsOpenp}>âž•</button>
                <br/>
                {IsOpenp && <Popup
                    content={<>
                        <div>
                            <label>Folder name</label>
                            <br/>
                            <input
                                type="text"
                                name="name"
                                value={name}
                                onChange={changeHandler}
                            />
                        </div>
                        <div>
                            <button type="submit" onClick={(e)=>submit(e)}>Submit</button>
                        </div>
                        </>}
                        handleClose={togglePopup}
                        />}
                {elem.map((el, i) =>

                <button
                    style={{
                    display: isActive ? 'none' : '',
                    backgroundColor: 'green',
                    color: 'white',
                    padding: '75px 85px',
                    textAlign: 'center',
                    margin: '24px 47px',
                    fontSize: '34px',
                    }} 
                    type="button" 
                    onClick={() => { handleClick(); setIsOnclick(elem[i])}}>{elem[i]}</button>)}
                {isOpen1 && <Exercise isOpen = {isOpen} filename={isOnclick} />}
            </div>
    )
}
