import React, {Component} from "react";
import Table from "./Table";
import axios from "axios";
//import {okClick, cancelClick} from "./Update"

class Tblu extends Component {
    constructor(){
        super()
        this.state = {
            elem : [],
            newform: ""
        }
        this.addForm = this.addForm.bind(this)
    }

    componentDidMount(){
        axios.get('http://localhost:5000/GET')
        .then(res => {
            const elems = res.data
            this.setState({
                elem: elems                
            })
        })
    }

    handleSubmit(event) {
        console.log("am intrat")
        event.preventDefault();
        console.log(event.target)
        // const user = {
        //     FirstName: this.state.FirstName,
        //     LastName: this.state.LastName,
        //     height: this.state.height
        // };

        axios.post(`http://127.0.0.1:5000/add` )
            .then(res => {
            console.log(res);
            console.log(res.data);
            })
    }

    deleteMe(ID){
        axios.delete(`http://127.0.0.1:5000/GET/${ID}`)
          .then(res => {
            var elem = document.getElementById(ID)
            elem.remove();
            console.log(res)
          })
    }
    update(oldValue, field, ID, newValue){
        // let html = 
        // `<input id="input-${field}-${ID}" type="text" name="${field}" value="${newValue}">&nbsp
        // <button id="cancel-${ID}">❌</button>&nbsp
        // <button id="ok-${ID}">✅</button>`;
        // console.log(oldValue)
    //     oldValue.innerHTML = html;
    //     var ok = document.getElementById(`ok-${ID}`);
    //     var cancel = document.getElementById(`cancel-${ID}`);
    //     var inputElem = document.getElementById(`input-${field}-${ID}`);
    //     ok.addEventListener("click", function() { okClick(inputElem, oldValue, ID, field) });
    //     cancel.addEventListener("click", function() { cancelClick(oldValue, newValue) });
    }

    addForm(){
        const form = `<div>
        <form onSubmit=${this.handleSubmit}>

          <label>
            FirstName:
            <input type="text" name="FirstName"/>
          </label>
          <label>
            FirstName:
            <input type="text" name="FirstName"/>
          </label>
          <label>
            LastName:
            <input type="text" name="LastName"/>
          </label>
          <label>
            height:
            <input type="number" name="height"/>
          </label>
          <button type="submit">Add</button>
        </form>
        </div>`;
        this.setState({newform: form})

    }

    render() {
        return (
            <body>
                <div>
                    <div id="myDynamicTable" onClick={this.addForm}>➕</div>
                    <div className="content" dangerouslySetInnerHTML={{__html: this.state.newform}}></div>
                </div>
            <table >
                <tr > 
                    <th>ID</th>
                    <th>Photo</th>
                    <th>FirstName</th>
                    <th>LastName</th>
                    <th>height</th>
                    <th>Delete</th>
                </tr>
                {this.state.elem.map((el, i) => 
                <Table ID={i+1} 
                    id={this.state.elem[i].ID} 
                    thumbnail={this.state.elem[i].thumbnail} 
                    FirstName={this.state.elem[i].FirstName} 
                    LastName={this.state.elem[i].LastName} 
                    height={this.state.elem[i].height} 
                    deleteMe={this.deleteMe} 
                    update={this.update}
                    />)}
                </table>
            </body>

        )
    }

}

export default Tblu
