import React, { Component } from 'react'
import axios from 'axios'
import Popup from './Popup'

class PostForm extends Component {
	constructor(props) {
		super(props)

		this.state = {
			selectedFile:'',
			FirstName: '',
			LastName: '',
			height: '',

		}
	}
	handleInputChange = (event) => {
        this.setState({
			selectedFile: event.target.files[0],
		})
    }

	changeHandler = (e) => {
		this.setState({ 
			[e.target.name]: e.target.value })
	}

	submit = (e) => {
		const data = new FormData() 
        data.append('photo', this.state.selectedFile)
        data.append('FirstName', this.state.FirstName)
        data.append('LastName', this.state.LastName)
        data.append('height', this.state.height)

		e.preventDefault()

		axios.post('http://127.0.0.1:5000/add', data, {})	
		.then(() => {
			this.props.this.setState({reRender: !this.props.this.reRender})
		})	
	}



	render() {
		const {photo, FirstName, LastName, height } = this.state
		console.log(this.state)
		return (
			props.isOpen && < Popup
			content={<div>
				<div>
					<input
						type="file"
						name="photo"
						value={photo}
						onChange={this.handleInputChange}
					/>
				</div>
				<div>
					<input
						type="text"
						name="FirstName"
						value={FirstName}
						onChange={this.changeHandler}
					/>
				</div>
				<div>
					<input
						type="text"
						name="LastName"
						value={LastName}
						onChange={this.changeHandler}
					/>
				</div>
				<div>
					<input
						type="text"
						name="height"
						value={height}
						onChange={this.changeHandler}
					/>
				</div>
				<button type="submit" onClick={(e)=>this.submit(e)}>Submit</button>
			</div>}

			/>
		)
	}
}

export default PostForm
