                    {this.state.isOpen ? <PostForm this={this} isOpen={this.state.isOpen}/> : <div></div>}
                    <div className="content" dangerouslySetInnerHTML={{__html: this.state.newform}}></div>
                </div>
            <table >
                <tr > 
                    <th>ID</th>
                    <th>Photo</th>
                    <th>FirstName</th>
                    <th>LastName</th>
                    <th>height</th>
                    <th>Delete</th>
                    <th>Edit</th>
                </tr>

                {this.state.elem.map((el, i) => <Table ID={i+1} 
                    id={this.state.elem[i].ID} 
                    thumbnail={this.state.elem[i].thumbnail} 
                    FirstName={this.state.elem[i].FirstName} 
                    LastName={this.state.elem[i].LastName} 
                    height={this.state.elem[i].height} 
                    deleteMe={this.deleteMe}
                    updateMe={this.updateMe}/>)
                }
                </table>
            </body>
        )
    }

}

export default Tblu
