import React, {useState, useEffect} from "react"; 
import axios from "axios";
import Popup from "./Popup";
import "./style.css";

import '../../node_modules/font-awesome/css/font-awesome.min.css'; 


export default function Exercise({isOpen, filename}) {
  const [IsOpenp, setIsOpenp] = useState(isOpen);
  const [isOpen1, setIsOpen] = useState(false);
  const [child, setChild] = useState([]);
  const [name, setName] = useState();
  const [another, setAnother] = useState("");
  const [isActive, setIsActive] = useState(false);
  const [isOnclick, setIsOnclick] = useState("");

  const changeHandler = (e) => {
		setName(e.target.value)
	}

  const handleClick = () => {
    setIsOpen(!isOpen);
    setIsActive(current => !current);
  }

  const togglePopup = () => {
    setIsOpenp(!IsOpenp);
  }

  const getChild = (filename) => {
    var new1 = []
    axios.get('http://localhost:5000/' + filename)
    .then(res => {
        const elems = res.data
        for (let i=0; i<=elems.length; i++){
          if (elems[i].split(".")[1] === 'txt'){

              setAnother(elems[i])
          }
          else{
              new1.push(elems[i])
          }
          setChild(new1)
        }
    })
  }

  const submit = (e) => {
		const data = new FormData() 
        data.append('name', filename + "/" + name)
		e.preventDefault()

		axios.post('http://127.0.0.1:5000/add', data,  {})	
		.then(() => {
			getChild(filename)
		})	
		.catch(error => console.error(error))
	}

  useEffect(() => {
    getChild(filename)
}, [filename]);

  return (
    <div>
      <i className="fa fa-plus"
        style={{
          display: isActive ? 'none' : '',
          color: 'green',
          padding: '25px 25px',
          fontSize: '37px',
        }}

        onClick={setIsOpenp}></i>
        <br/>
        {IsOpenp && <Popup
          content={<>
            <div>
              <label>Folder name</label>
              <br/>
              <input
                type="text"
                name="name"
                value={name}
                onChange={changeHandler}
              />
              </div>
              <div>
                  <button type="submit" onClick={(e)=>submit(e)}>Submit</button>
              </div>
        </>}
        handleClose={togglePopup}
        />}
      {child.map((el, i) => 
      <i className="fa fa-folder" aria-hidden="true"
      style={{
        display: isActive ? 'none' : '',
        color: 'black',
        padding: '35px 55px',
        textAlign: 'center',
        margin: '14px 27px',
        fontSize: '67px',
      }} 
      onClick={() => {handleClick(); setIsOnclick(child[i])}}><p
      style={{
        color: 'black',
        textAlign: 'center',
        fontSize: '17px',
      }}>{child[i]}</p></i>

      )}
      {another !== "" && 
      <i className="fa-solid fa-files"
      style={{
        display: isActive ? 'none' : '',
        color: 'black',
        padding: '35px 55px',
        textAlign: 'center',
        margin: '14px 27px',
        fontSize: '67px',
      }} 
      onClick={() => {handleClick(); setIsOnclick(another)}}><p
      style={{
        color: 'black',
        textAlign: 'center',
        fontSize: '17px',
      }}>{another}</p></i>

      }
      {isOpen1 && <Exercise isOpen={isOpen} filename={filename + "/" + isOnclick}/>}
    </div>
)
}
