import React, {useState, useEffect} from "react"; 
import axios from "axios";
import Popup from "./Popup";

export default function Exercise({isOpen, filename, getElem}) {
  const [IsOpenp, setIsOpenp] = useState(isOpen);
  const [isOpen1, setIsOpen] = useState(false);
  const [child, setChild] = useState([]);
  const [name, setName] = useState();
  const [isActive, setIsActive] = useState(false);

  const changeHandler = (e) => {
		setName(e.target.value)
	}
  const handleClick = () => {
    setIsOpen(!isOpen);
    setIsActive(current => !current);
  }
  const togglePopup = () => {
    setIsOpenp(!IsOpenp);
  }
  const getChild = (filename) => {
    axios.get('http://localhost:5000/' + filename)
    .then(res => {
        const elems = res.data
        console.log(elems)
        setChild(elems)
    })
  }
  const submit = (e) => {
		const data = new FormData() 
        data.append('name', filename + "/" + name)
		e.preventDefault()

		axios.post('http://127.0.0.1:5000/add', data,  {})	
		.then(() => {
			getChild
		})	
		.catch(error => console.error(error))
	}
  useEffect(() => {
    getChild(filename)
}, []);
  return (
    <div>
        <button
            style={{
                display: isActive ? 'none' : '',
            }}
             type="submit" 
             onClick={setIsOpenp}>+</button>
            <br/>
            {IsOpenp && <Popup
					content={<>
                  <div>
                      <label>Folder name</label>
                      <br/>
							<input
								type="text"
								name="name"
								value={name}
								onChange={changeHandler}
							/>
						</div>
                        <div>
                            <button type="submit" onClick={(e)=>submit(e)}>Submit</button>
                        </div>
					</>}
					handleClose={togglePopup}
					/>}
        {child.map((el, i) => 
        <button 
          style={{
          display: isActive ? 'none' : '',
          backgroundColor: 'green',
          color: 'white',
          padding: '75px 85px',
          textAlign: 'center',
          margin: '24px 47px',
          fontSize: '37px',
         }} 
          type="button" 
          onClick={handleClick}>{child[i]}</button>)}
        {isOpen1 && <Exercise isOpen={isOpen} filename={filename + "/" + child[0]} getElem={getElem}/>}
    </div>
)


}
