import React, { Component } from 'react'
import axios from 'axios'
class PostForm extends Component {
	constructor(props) {
		super(props)

		this.state = {
			photo: '',
			FirstName: '',
			LastName: '',
			height: ''
		}
	}

	changeHandler = e => {
		this.setState({ 
			[e.target.name]: e.target.value,
			photo: e.target.files[0]
		 })
	}

	submitHandler = e => {
		e.preventDefault()
		const data = new FormData() 
        data.append('file', this.state.selectedFile)
		console.log(this.state)
		axios
			.post('http://127.0.0.1:5000/add', this.state)
			.then(response => {
				console.log(response)
			})
			.catch(error => {
				console.log(error)
			})
	}

	render() {
		const {photo, FirstName, LastName, height } = this.state
		console.log(this.state)
		return (
			<div>
				<form onSubmit={this.submitHandler}>
				<div>
						<input
							type="file"
							name="photo"
							value={photo}
							onChange={this.changeHandler}
						/>
					</div>
					<div>
						<input
							type="text"
							name="FirstName"
							value={FirstName}
							onChange={this.changeHandler}
						/>
					</div>
					<div>
						<input
							type="text"
							name="LastName"
							value={LastName}
							onChange={this.changeHandler}
						/>
					</div>
					<div>
						<input
							type="text"
							name="height"
							value={height}
							onChange={this.changeHandler}
						/>
					</div>
					<button type="submit">Submit</button>
				</form>
			</div>
		)
	}
}

export default PostForm
