import React, {useState} from "react";
import PutForm from "./PutForm";
import Popup from "./Popup";
import "./style.css";

function Table(ID, id, thumbnail, FirstName, LastName){
    const [isOpen, setIsOpen] = useState(false);
    const [deleteOpen, setdeleteOpen] = useState(false);
    const [deleteClick, setdeleteClick] = useState(false);
    const deleteToggle = () => {
        setdeleteOpen(!deleteOpen);
    }
    const deleteAmu = () => {
        setdeleteClick(!deleteClick)
    }
    const togglePopup = () => {
        setIsOpen(!isOpen);
    }
    let thub = props.thumbnail
    let source = "/imag/" + thub
    return (
        <tr id={props.id} >
            <td >{props.ID}</td> 
            <td><img src={source} alt=""/></td>
            <td >{props.FirstName }</td>
            <td >{props.LastName }</td>
            <td >{props.height}</td>

            <td>
                <button onClick={deleteToggle} >üóë</button>
                {deleteOpen ? <Popup 
                content={<>
                    <div>
                        <p>Are you sure??</p>
                        <button onClick={deleteAmu}>Delete</button>
                    </div>
                    </>} 
                    handleClose={deleteToggle}
                    /> : <div></div>}
                {deleteClick ? props.deleteMe(props.id) : <div></div>}
            </td>
            <td>
                <button onClick={togglePopup} >üìù</button>
                {isOpen ? <PutForm getElem={props.getElem} 
                            id={props.id} 
                            FirstName={props.FirstName} 
                            LastName={props.LastName} 
                            height={props.height}
                            photo = {props.thumbnail}/> : <div></div> }
            </td>
        </tr>   
    )
}

export default Table;
