import React, {Component} from "react";
import Table from "./Table";
import axios from "axios";
import PostForm from "./PostForm";
//import {okClick, cancelClick} from "./Update"

class Tblu extends Component {
    constructor(){
        super()
        this.state = {
            elem : [],
            addElem: false,
            reRender: false
        }
        this.add = this.add.bind(this)

    }

    componentDidMount(){
        axios.get('http://localhost:5000/GET')
        .then(res => {
            const elems = res.data
            this.setState({
                elem: elems                
            })
        })
    }
    add(){
        this.setState({
            addElem: true
        })
    }


    deleteMe(ID){
        axios.delete(`http://127.0.0.1:5000/GET/${ID}`)
          .then(res => {
            var elem = document.getElementById(ID)
            elem.remove();
            console.log(res)
          })
    }



    render() {
        if (this.state.reRender) {
            return (<body>
            <div>
                <div id="myDynamicTable" onClick={this.add}>➕</div>
                {this.state.addElem ? <PostForm reRender={this.state.reRender}/> : <div></div>}
                <div className="content" dangerouslySetInnerHTML={{__html: this.state.newform}}></div>
            </div>
        <table >
            <tr > 
                <th>ID</th>
                <th>Photo</th>
                <th>FirstName</th>
                <th>LastName</th>
                <th>height</th>
                <th>Delete</th>
            </tr>
            {this.state.elem.map((el, i) => 
            <Table ID={i+1} 
                id={this.state.elem[i].ID} 
                thumbnail={this.state.elem[i].thumbnail} 
                FirstName={this.state.elem[i].FirstName} 
                LastName={this.state.elem[i].LastName} 
                height={this.state.elem[i].height} 
