import React, {useState, useEffect} from "react"; 
import axios from "axios";
import Exercise from "./Exercise.js";
import Popup from './Popup';
import "./style.css";


export default function Tblu({isOpen}) {
    const [IsOpenp, setIsOpenp] = useState(isOpen);
    const [isOpen1, setIsOpen] = useState(false);
    const [elem, setElem] = useState([]);
    const [name, setName] = useState();
    const [isActive, setIsActive] = useState(false);

    const changeHandler = (e) => {
		setName(e.target.value)
	}
    const handleClick = () => {
        setIsOpen(!isOpen);
    }
    const togglePopup = () => {
        setIsOpenp(!IsOpenp);
    }
    const getElem = () => {
        axios.get('http://localhost:5000/')
        .then(res => {
            const elems = res.data
            console.log(elems)
            setElem(elems)
        })
    }
    const submit = (e) => {

		// e.preventDefault()

		// axios.post('http://127.0.0.1:5000/add', data, {})	
		// .then(() => {
		// 	getElem()
		// })	
		// .catch(error => console.error(error))
	}

    useEffect(() => {
        getElem()
    }, []);

    return (
        <div>
            <button type="submit" onClick={setIsOpenp}>âž•</button>
            {IsOpenp && <Popup
					content={<>
                        <div>
                            <label>Folder name</label>
                            <br/>
							<input
								type="text"
								name="name"
								value={name}
								onChange={changeHandler}
							/>
						</div>
                        <div>
                            <button type="submit" onClick={(e)=>submit(e)}>Submit</button>
                        </div>
					</>}
					handleClose={togglePopup}
					/>}
            {elem.map((el, i) =>
            <button
                style={{
                display: none,
              }} type="button" onClick={handleClick} >{elem[i]}</button>            
            )
            }
            {isOpen1 && <Exercise filename={elem[0]}/>}

        </div>
    )




}
