import React, {useState} from "react";
import PutForm from "./PutForm";
import Popup from "./Popup";
import "./style.css";

function Table(props){
    const [isOpen, setIsOpen] = useState(false);
    const [deleteOpen, setdeleteOpen] = useState(false);

    const deleteToggle = () => {
        setdeleteOpen(!deleteOpen);
    }
    const togglePopup = () => {
        setIsOpen(!isOpen);
    }
    let thub = props.thumbnail
    let source = "/imag/" + thub
    return (

        <tr id={props.id} >
            <td >{props.ID}</td> 
            <td><img src={source} alt=""/></td>
            <td >{props.FirstName }</td>
            <td >{props.LastName }</td>
            <td >{props.height}</td>

            <td>
                <button onClick={deleteToggle} >üóë</button>
                {deleteOpen ? <Popup content={<><p></p><button onClick={props.deleteMe(props.id)}>Delete</button></>}handleClose={this.togglePopup}/> : <div></div>}
            </td>
            <td>
                <button onClick={togglePopup} >üìù</button>
                {isOpen ? <PutForm getElem={props.getElem} id={props.id} FirstName={props.FirstName} LastName={props.LastName} height={props.height}/> : <div></div> }
            </td>
        </tr>   
    )
}

export default Table;
