import React, { Component } from 'react'
import axios from 'axios'
import Popup from './Popup'

class PutForm extends Component {
	constructor(props) {
		super(props)

		this.state = {
			isOpen: true,
			FirstName: 'props',
			LastName: '',
			height: '',

		}
		this.togglePopup = this.togglePopup.bind(this)
	}
	togglePopup() {
		this.setState({isOpen: !this.state.isOpen});
	}


	changeHandler = (e) => {
		this.setState({ 
			[e.target.name]: e.target.value })
	}

	submit = (e) => {
		const data = new FormData() 
        data.append('FirstName', this.state.FirstName)
        data.append('LastName', this.state.LastName)
        data.append('height', this.state.height)

		e.preventDefault()

		axios.post('http://127.0.0.1:5000/add', data, {})	
		.then(() => {
			this.props.this.setState({reRender: !this.props.this.reRender})
		})	
	}



	render() {
		const {FirstName, LastName, height } = this.state
		console.log(this.state)
		return (<div>
			{this.state.isOpen && <Popup
			content={<>
				<div>
				<div>
					<input
						type="text"
						name="FirstName"
						value={FirstName}
						onChange={this.changeHandler}
					/>
				</div>
				<div>
					<input
						type="text"
						name="LastName"
						value={LastName}
						onChange={this.changeHandler}
					/>
				</div>
				<div>
					<input
						type="text"
						name="height"
						value={height}
						onChange={this.changeHandler}
					/>
				</div>
				<button type="submit" onClick={(e)=>this.submit(e)}>Submit</button>
			</div>
			</>}
			handleClose={this.togglePopup}

			/>}
			</div>
		)
	}
}

export default PutForm
