        super()
        this.state = {
            elem : []
            // ID: 0,
        }
    }
    componentDidMount(){
        fetch('http://localhost:5000/GET',{
            'methods':'GET',
            headers : {
              'Content-Type':'application/json'
            }
        })
        .then(response => response.json())
        .then(data => {
            this.setState({
                elem: data
            })
        })
          .catch(error => console.log(error))
        }

