                <th>LastName</th>
                <th>height</th>
                <th>Delete</th>
            </tr>
            {this.state.elem.map((el, i) => <Table ID={i+1} thumbnail={this.state.elem[i].thumbnail} FirstName={this.state.elem[i].FirstName} LastName={this.state.elem[i].LastName} height={this.state.elem[i].height} deleteMe={this.deleteMe} update={this.update}/>)}
