import React, {Component} from "react";
import Table from "./Table";
import axios from "axios";

class Tblu extends Component {
    state = {
        elem : []
    }

    componentDidMount(){
        axios.get('http://localhost:5000/GET')
        .then(res => {
            const elems = res.data
            this.setState({
                elem: elems
            })
        })
        }

    deleteMe(ID){
        axios.delete(`http://127.0.0.1:5000/GET/${ID}`)
          .then(res => {
            var elem = document.getElementById(ID)
            elem.remove();
            console.log(res)
          })
    }

    okClick(input, oldValue, updatedID, field){
        let updateForm = new FormData();
        updateForm.append(field, input.value);
        axios.put(`http://127.0.0.1:5000/update/${field}/${updatedID}`, {updateForm})
        .then(res => {
            if(res.status == 200){
            var valueInput = input.value;
            oldValue.innerHTML = valueInput;
            }  
        })
    }
    cancelClick(oldValue, newValue) {
        oldValue.innerHTML = newValue;
    }

    update(oldValue, field, ID, newValue){
        let html = 
        `<input id="input-${field}-${ID}" type="text" name="${field}" value="${newValue}">&nbsp
        <button id="cancel-${ID}">❌</button>&nbsp
        <button id="ok-${ID}">✅</button>`;
        oldValue.innerHTML = html;
        var ok = document.getElementById(`ok-${ID}`);
        var cancel = document.getElementById(`cancel-${ID}`);
        var inputElem = document.getElementById(`input-${field}-${ID}`);
        ok.addEventListener("click", function() { okClick(inputElem, oldValue, ID, field) });
        cancel.addEventListener("click", function() { cancelClick(oldValue, newValue) });
    }




    render() {
        console.log(this.state.elem)
        return (
            <table >
            <tr > 
                <th>ID</th>
                <th>Photo</th>
                <th>FirstName</th>
                <th>LastName</th>
                <th>height</th>
                <th>Delete</th>
            </tr>
            {this.state.elem.map((el, i) => 
            <Table ID={i+1} 
                id={this.state.elem[i].ID} 
                thumbnail={this.state.elem[i].thumbnail} 
                FirstName={this.state.elem[i].FirstName} 
                LastName={this.state.elem[i].LastName} 
                height={this.state.elem[i].height} 
                deleteMe={this.deleteMe} 
                update={this.update}/>)}
            </table>
        )
    }

}

export default Tblu
